package com.pp.peterpet.tour;

public class TourDTO {

	
		private String p_area;
		private String p_category;
		private String p_name;
		private String p_address;
		private String p_tell;
		private String p_des;
		private String p_img;
		private String p_code;

		public TourDTO() {
			// TODO Auto-generated constructor stub
		}

		public TourDTO(String p_area, String p_category, String p_name, String p_address, String p_tell, String p_des,
				String p_img, String p_code) {
			super();
			this.p_area = p_area;
			this.p_category = p_category;
			this.p_name = p_name;
			this.p_address = p_address;
			this.p_tell = p_tell;
			this.p_des = p_des;
			this.p_img = p_img;
			this.p_code = p_code;
		}

		public String getP_area() {
			return p_area;
		}

		public void setP_area(String p_area) {
			this.p_area = p_area;
		}

		public String getP_category() {
			return p_category;
		}

		public void setP_category(String p_category) {
			this.p_category = p_category;
		}

		public String getP_name() {
			return p_name;
		}

		public void setP_name(String p_name) {
			this.p_name = p_name;
		}

		public String getP_address() {
			return p_address;
		}

		public void setP_address(String p_address) {
			this.p_address = p_address;
		}

		public String getP_tell() {
			return p_tell;
		}

		public void setP_tell(String p_tell) {
			this.p_tell = p_tell;
		}

		public String getP_des() {
			return p_des;
		}

		public void setP_des(String p_des) {
			this.p_des = p_des;
		}

		public String getP_img() {
			return p_img;
		}

		public void setP_img(String p_img) {
			this.p_img = p_img;
		}

		public String getP_code() {
			return p_code;
		}

		public void setP_code(String p_code) {
			this.p_code = p_code;
		}

		
		
}
